import randomdef lotto():    user_numbers = []    while len(user_numbers) < 6:        try:            user_input = int(input("Enter a number: "))            if user_input < 1 or user_input > 49:                print("NUMBERS MUST BE BETWEEN 1 AND 49!")                continue        except ValueError:            print("Invalid number!")            continue        except (user_input < 1 or user_input > 49):            print("numbers already entered!")        user_numbers.append(user_input)    user_numbers.sort()    print("Your Numbers:", user_numbers)    drawn_numbers = []    while len(drawn_numbers) < 6:        drawn_number = random.randint(1, 49)        if drawn_number not in drawn_numbers:            drawn_numbers.append(drawn_number)    drawn_numbers.sort()    print("Drawn numbers:", drawn_numbers)    matched_numbers = set(user_numbers) & set(drawn_numbers)    num_matched = len(matched_numbers)    if num_matched >= 3:        print("You matched", num_matched, "numbers!")    else:        print("Sorry, you didn't match enough numbers.")lotto()